import cv2


def resize_image(image, shape=(100, 100)):
    return cv2.resize(image, (shape[0], shape[0]))
    ...


def find_face(image , shape=(100 , 100)):
    face_cascade = cv2.CascadeClassifier(
        cv2.data.haarcascades + 'haarcascade_frontalface_default.xml')
    
    # Detect faces in the image
    faces = face_cascade.detectMultiScale(image, scaleFactor=1.3, minNeighbors=5)

    # Process each detected face
    detected = []
    for i, (x, y, w, h) in enumerate(faces):
        face_roi = image[y:y+h, x:x+w]
        face_roi = cv2.resize(face_roi , (shape[1] , shape[0]))
        detected.append((face_roi, face_roi.shape[0] * face_roi.shape[1]))
    if len(detected) > 0:
        return max(detected, key=lambda x: x[1])[0]
    else: return []
    ...


